package com.htc.corejava.day1;
/*
*Print all even numbers from 0 to 100. (Hint: Use the for loop statement).
*/
 public class EvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 2;
		while(number<=100) // Looping
		{
			System.out.println("Print Even Number " + number);
			number = number +2;
		}
		
	}

}
